import React, { useState } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import { Container, Box, Paper, Typography, Button, Select, MenuItem, FormControl, InputLabel } from '@mui/material';
import Editor from '@monaco-editor/react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import axios from 'axios';

const darkTheme = createTheme({
  palette: {
    mode: 'dark',
  },
});

const languages = [
  { id: 'py', name: 'Python' },
  { id: 'cpp', name: 'C++' },
  { id: 'java', name: 'Java' },
  { id: 'js', name: 'JavaScript' },
  { id: 'go', name: 'Go' }
];

function App() {
  const [code, setCode] = useState('# Write your Python code here\nprint("Hello, World!")');
  const [language, setLanguage] = useState('py');
  const [output, setOutput] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleExecute = async () => {
    setIsLoading(true);
    try {
      const response = await axios.post('http://localhost:8000/api/v1/execute', {
        language,
        code
      });
      setOutput(response.data.output);
      toast.success('Code executed successfully!');
    } catch (error) {
      setOutput(error.response?.data?.detail || 'An error occurred');
      toast.error('Error executing code');
    }
    setIsLoading(false);
  };

  return (
    <ThemeProvider theme={darkTheme}>
      <CssBaseline />
      <Container maxWidth="lg">
        <Box sx={{ my: 4 }}>
          <Typography variant="h3" component="h1" gutterBottom align="center">
            CodeCrate
          </Typography>
          
          <Box sx={{ mb: 2 }}>
            <FormControl sx={{ minWidth: 200 }}>
              <InputLabel>Language</InputLabel>
              <Select
                value={language}
                label="Language"
                onChange={(e) => setLanguage(e.target.value)}
              >
                {languages.map((lang) => (
                  <MenuItem key={lang.id} value={lang.id}>
                    {lang.name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Box>

          <Paper elevation={3} sx={{ height: '400px', mb: 2 }}>
            <Editor
              height="100%"
              defaultLanguage="python"
              value={code}
              onChange={(value) => setCode(value)}
              theme="vs-dark"
              options={{
                minimap: { enabled: false },
                fontSize: 14,
              }}
            />
          </Paper>

          <Box sx={{ mb: 2 }}>
            <Button
              variant="contained"
              color="primary"
              onClick={handleExecute}
              disabled={isLoading}
              sx={{ mr: 2 }}
            >
              {isLoading ? 'Executing...' : 'Run Code'}
            </Button>
          </Box>

          <Paper elevation={3} sx={{ p: 2, minHeight: '100px' }}>
            <Typography variant="h6" gutterBottom>
              Output:
            </Typography>
            <pre style={{ margin: 0, whiteSpace: 'pre-wrap' }}>
              {output || 'No output yet'}
            </pre>
          </Paper>
        </Box>
      </Container>
      <ToastContainer position="bottom-right" />
    </ThemeProvider>
  );
}

export default App;
